(626, 'San Diego Zoo', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">The San Diego Zoo is renowned as one of the best zoos in the world.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Adult - $45.00 (gate price $48.00 (you save $3.00)<br />\r\nChild - $35.00 (gate price $38.00 (you save $3.00)<br />\r\nTickets can be purchased at the Union office. (on-line purchase)</p>\r\n', '', '2015-04-01 18:17:33', '55', 7, '', '', '', '', '', NULL, 1),
(627, 'AAA of Arizona', '<p><span style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">$15 Enrollment fee will be waived on all new memberships &amp; members will receive up to 14 months of membership for the price of 12 months. Plus one household membership &quot;free&quot; for the first year. Existing members may add 1 &quot;new&quot; household member free thru renewal date.</span></p>\r\n', '', '2015-04-01 18:22:30', '55', 7, '', '', '', '', '', NULL, 1),
(628, 'Van Chevrolet Buick GMC', '<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li class="style15" style="color: rgb(255, 0, 0);">GM supplier pricing on any Chevrolet Buick GMC - below invoice plus any rebates (Exclusions: Corvette and specialty cars)</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">No dealer adds charges</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Free door edge guards</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">20% off all parts and accessories</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">FREE window tinting</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Two years of FREE scheduled maintenance - oil changes and tire rotations with the purchase of any new car, truck, or SUV</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Service loaners for Union members: scheduled appointments preset with &quot;service advisor union contact&quot; will receive a free service loaner on availability.</li>\r\n</ul>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Union members must contact Michael Benson at (480)348-8356 or Bob Mooney at (623) 512-5332 to receive any of the above Union discounts.</p>\r\n', '', '2015-04-01 18:23:24', '55', 7, '', '', '', '', '', NULL, 1),
(629, 'Union Made Checks', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">With these union-made checks, members can show their union pride with every check they write. The competitively priced union-printed checks feature a pro-union message in addition to your union&#39;s logo - plus you can customize with photos of your children or pets.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">You can also order convenient return-address labels with a similar design.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Union-made personal checks:<br />\r\n200 one-part checks starting at $9.95 (plus shipping &amp; handling)</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Union-made business checks:<br />\r\nAvailable in single-page laser &amp; 3-to-a-page ledger styles.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Return-address labels:<br />\r\n240 count = $10.95 (plus shipping &amp; handling)</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Order on-line at&nbsp;<a href="http://www.unionplus.com/benefits/" style="color: rgb(25, 32, 103);" target="_blank">www.unionplus.org</a>&nbsp;or call (888)864-6625.</p>\r\n', '', '2015-04-01 18:23:57', '55', 7, '', '', '', '', '', NULL, 1),
(630, 'Union Plus MasterCard', '<p><span class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Competitive interest rates, no annual fee, skip payment options. Apply online at&nbsp;</span><a href="http://www.unionplus.com/benefits/" style="color: rgb(25, 32, 103); font-family: Times; font-size: medium; line-height: normal;" target="_blank">www.unionplus.org</a><span style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">&nbsp;</span><span class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">or call (888)522-4000.</span></p>\r\n', '', '2015-04-01 18:24:44', '55', 7, '', '', '', '', '', NULL, 1),
(631, 'American Income Partners', '<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Up to 60% savings on:</p>\r\n\r\n<ul class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Eye Care</li>\r\n	<li>Hearing Aids</li>\r\n	<li>Prescription Drugs</li>\r\n	<li>Chiropractic Care</li>\r\n</ul>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">at no cost to the Union Member.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Contact your Union Representative for additional information and instructions.</p>\r\n', '', '2015-04-01 18:25:21', '55', 7, '', '', '', '', '', NULL, 1),
(632, 'Employee Assistance Program or EMAP ', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">When you have a problem with:</p>\r\n\r\n<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Alcohol</li>\r\n	<li>drugs</li>\r\n	<li>child &amp; adolescent</li>\r\n	<li>family</li>\r\n	<li>gambling</li>\r\n	<li>marriage</li>\r\n	<li>financial/legal</li>\r\n</ul>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><span class="style15" style="color: rgb(255, 0, 0);">Get help! Open 24 hours a day, 7 days a week. (800)464-7101</span>.</p>\r\n', '', '2015-04-01 18:26:03', '55', 7, '', '', '', '', '', NULL, 1),
