(725, 'Alamo Car Rental', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Welcome to Alamo&reg; wheree UFCW Local 99 Members<span class="style15" style="color: rgb(255, 0, 0);">receive unlimited mileage and year-round discounts of Up to 20%</span>&nbsp;off Alamo&rsquo;s already great rates. For nearly 30 years, Alamo has served family and leisure travelers with fun, friendly service while operating more than 1,200 locations throughout the United States, Canada, Europe, Latin America and the Caribbean.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Make a reservation online or call 1-800-462-5266. Be sure to contact the Union office first and request the discount contract ID number to receive your discount.</p>\r\n', '', '2015-10-31 14:06:05', '53.2', 7, '0632ccad00.png', '', '', '', '', '', NULL, 1),
(726, 'AAA Arizona', '$15 Enrollment fee will be waived ', '<p><span style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">$15 Enrollment fee will be waived on all new memberships &amp; members will receive up to 14 months of membership for the price of 12 months. Plus one household membership &quot;free&quot; for the first year. Existing members may add 1 &quot;new&quot; household member free thru renewal date.</span></p>\r\n', '', '2015-10-31 14:08:28', '53.3', 7, '07c92499a4.png', '', '', '', '', '', NULL, 1),
(727, 'Van Chevrolet Buick GMC', 'Union members must contact Michael Benson at (480)348-8356 or Bob Mooney at (623) 512-5332 to receive any of the above Union discounts.', '<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li class="style15" style="color: rgb(255, 0, 0);">GM supplier pricing on any Chevrolet Buick GMC - below invoice plus any rebates (Exclusions: Corvette and specialty cars)</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">No dealer adds charges</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Free door edge guards</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">20% off all parts and accessories</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">FREE window tinting</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Two years of FREE scheduled maintenance - oil changes and tire rotations with the purchase of any new car, truck, or SUV</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Service loaners for Union members: scheduled appointments preset with &quot;service advisor union contact&quot; will receive a free service loaner on availability.</li>\r\n</ul>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Union members must contact Michael Benson at (480)348-8356 or Bob Mooney at (623) 512-5332 to receive any of the above Union discounts.</p>\r\n', '', '2015-10-31 14:10:10', '53.4', 7, '8bb65b768e.png', '', '', '', '', '', NULL, 1),
(728, 'Fellows Tax & Financial Services', '20% discount on income tax preparation', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Tax planning and financial services.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><span class="style15" style="color: rgb(255, 0, 0);">20% discount on income tax preparation. On-line filing is now available at&nbsp;</span><a href="http://www.1040.com/fellowstaxservice/" style="color: rgb(25, 32, 103);" target="_blank">www.1040.com/fellowstaxservice</a>&nbsp;.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Call (623)979-1220 and ask for the UFCW Union discount. Union membership will be verified with the Union.</p>\r\n', '', '2015-10-31 14:12:59', '53.5', 7, '32236ab09b.png', '', '', '', '', '', NULL, 1),
(729, 'American Income Partners', 'Up to 60% savings on Medical Services', '<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Up to 60% savings on:</p>\r\n\r\n<ul class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Eye Care</li>\r\n	<li>Hearing Aids</li>\r\n	<li>Prescription Drugs</li>\r\n	<li>Chiropractic Care</li>\r\n</ul>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">at no cost to the Union Member.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Contact your Union Representative for additional information and instructions.</p>\r\n', '', '2015-10-31 14:15:28', '53.6', 7, 'aeb50ac479.png', '', '', '', '', '', NULL, 1),
(730, 'All American Clothing', 'Union Made Clothing:  All American Clothing Company, Justice Clothing Company, No Sweat Apparel', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><strong>Wear your union pride on your sleeve...or pant leg...with union-made clothing!&nbsp;</strong>These new Union Plus Clothing Discounts are designed by and for union members only. What&#39;s in it for you? A 5-10% discount on union-made clothing from three providers of union-made apparel.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Find the union label on everything from union-made jeans, T-shirts and sweats to socks, dresses and pants. And save on every purchase with your union-only family discount - even order gift certificates for your friends.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">All American Clothing Company offers 10% discount off of all orders.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Justice Clothing Company offers 5% discount off of all orders.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">http://www.allamericanclothing.com/unionplus.html</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">No Sweat Apparel offers 5% off of all orders.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Call the Union office to request discount information and codes.</p>\r\n', '', '2015-10-31 14:18:30', '53.7', 7, '2ac9f43b82.png', '', '', '', '', '', NULL, 1),
