(768, 'Sea World', 'San Diego, California  AQUATICA WATERPARK', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">SeaWorld San Diego is one of the world&#39;s premier marine adventure parks with 200 acres of world-class shows, thrilling rides and unforgettable animal encounters. More than 80 million visitors have explored the mysteries of the sea here, with up-close animal interactions and exhilarating thrills.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><strong>SEA WORLD</strong><br />\r\nAdult - $60.00 (gate price $86.00-you save $26.00)<br />\r\nChild (3 yrs - 9 yrs.) - $60.00 (gate price $80.00-you save $20.00)<br />\r\nTickets can be purchased at the Union office.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><strong>AQUATICA WATERPARK - one day ticket</strong>&nbsp;<span class="style31" style="color: rgb(0, 0, 0); font-weight: bold;">(seasonal)</span><br />\r\nAdult - $28.00 (gate price $38.00 - you save $10.00)<br />\r\nChild - $20.00 (gate price $30.00 - you save $10.00)</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '2015-11-21 12:23:40', '61', 7, '5b165da787.png', '', '', '', '', '', NULL, 1),
(769, 'Union-made Checks', '200 one-part checks starting at $9.95 (plus shipping & handling)', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">With these union-made checks, members can show their union pride with every check they write. The competitively priced union-printed checks feature a pro-union message in addition to your union&#39;s logo - plus you can customize with photos of your children or pets.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">You can also order convenient return-address labels with a similar design.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Union-made personal checks:<br />\r\n200 one-part checks starting at $9.95 (plus shipping &amp; handling)</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Union-made business checks:<br />\r\nAvailable in single-page laser &amp; 3-to-a-page ledger styles.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Return-address labels:<br />\r\n240 count = $10.95 (plus shipping &amp; handling)</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Order on-line at&nbsp;<a href="http://www.unionplus.com/benefits/" style="color: rgb(25, 32, 103);" target="_blank">www.unionplus.org</a>&nbsp;or call (888)864-6625.</p>\r\n', '', '2015-11-21 10:43:11', '65', 7, '69787f739c.jpg', '', '', '', '', '', NULL, 1),
(770, 'LinkedUnion', '', '<h3 style="margin: 0px 0px 8px; padding: 0px; border: 0px; font-stretch: inherit; font-size: 28px; line-height: 34px; font-family: Roboto, ''Trebuchet MS'', Helvetica, Arial, sans-serif; vertical-align: baseline; color: rgb(41, 41, 41);">Make Connections Easier</h3>\r\n\r\n<p style="margin: 0px 0px 20px; padding: 0px; border: 0px; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto, ''Trebuchet MS'', Helvetica, Arial, sans-serif; vertical-align: baseline; color: rgb(66, 66, 66); letter-spacing: 0.2px;">LinkedUnion doesn&rsquo;t just assist in sending messages and sharing information on a mobile application with your members, we allow you to tailor it to each members&rsquo;s preferences, creating better informed members.</p>\r\n\r\n<p style="margin: 0px 0px 20px; padding: 0px; border: 0px; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto, ''Trebuchet MS'', Helvetica, Arial, sans-serif; vertical-align: baseline; color: rgb(66, 66, 66); letter-spacing: 0.2px;">LinkedUnion allows for personalized messaging. &nbsp;Members choose how they receive their messages and LinkedUnion does the rest.</p>\r\n\r\n<p style="margin: 0px 0px 20px; padding: 0px; border: 0px; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto, ''Trebuchet MS'', Helvetica, Arial, sans-serif; vertical-align: baseline; color: rgb(66, 66, 66); letter-spacing: 0.2px;">Built Union for Union</p>\r\n', '', '2015-11-21 13:31:52', '80', 7, 'f3cd9fd005.png', '', '', '', '', '', NULL, 1),
(771, 'US Airways Center', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Discount tickets on select shows, i.e.&nbsp;<strong>Disney on Ice, Ringling Bros. and Barnum Bailey Circus</strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Contact the Union office for information and pricing on selection show.</p>\r\n', '', '2015-11-21 13:33:17', '81', 7, 'c82ce2f0be.png', '', '', '', '', '', NULL, 1),
(773, 'Hertz Car Rental', '', '<p><span style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Save $20 on your weekly rental of a compact or higher class vehicle, plus an additional discount when using the union discount code. Contact the Union office and request your discount code.</span></p>\r\n', '', '2015-11-21 10:29:01', '62', 7, 'ec4c504650.png', '', '', '', '', '', NULL, 1),
(774, 'Budget Car Rental', '', '<p><span style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Save $25.00 off a weekly rental or a Free Weekend Day. Contact the Union office for contact number and discount code.</span></p>\r\n', '', '2015-11-21 10:29:59', '62', 7, '3cf78859e9.png', '', '', '', '', '', NULL, 1),
(775, 'Avis Car Rental', '', '<p><span style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Save $30.00 off a weekly rental or a Free Weekend Day. Contact the Union office for contact number and discount code.</span></p>\r\n', '', '2015-11-21 10:30:43', '62', 7, '1cb7986c37.png', '', '', '', '', '', NULL, 1),
(776, 'Alamo Car Rental', 'Make a reservation online or call 1-800-462-5266.', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Welcome to Alamo&reg; where UFCW Local 99 Members&nbsp;<span class="style15" style="color: rgb(255, 0, 0);">receive unlimited mileage and year-round discounts of Up to 20%</span>&nbsp;off Alamo&rsquo;s already great rates. For nearly 30 years, Alamo has served family and leisure travelers with fun, friendly service while operating more than 1,200 locations throughout the United States, Canada, Europe, Latin America and the Caribbean.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Make a reservation online or call 1-800-462-5266. Be sure to contact the Union office first and request the discount contract ID number to receive your discount.</p>\r\n', '', '2015-11-21 10:32:13', '62', 7, 'd8e9ece5e5.png', '', '', '', '', '', NULL, 1),
(777, 'AAA Arizona', '$15 Enrollment fee will be waived on all new memberships ', '<p><span style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">$15 Enrollment fee will be waived on all new memberships &amp; members will receive up to 14 months of membership for the price of 12 months. Plus one household membership &quot;free&quot; for the first year. Existing members may add 1 &quot;new&quot; household member free thru renewal date.</span></p>\r\n', '', '2015-11-21 10:38:29', '63', 7, '979bd016a0.png', '', '', '', '', '', NULL, 1);
