(778, 'Take Charge America ', 'Should you need financial guidance, please call TCA at 1-800-823-7396 or visit www.takechargeamerica.org', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Since 1987, TCA has helped more than 1.6 million people nationwide manage their personal debts and finances. Services include:</p>\r\n\r\n<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Credit Counseling: a free, confidential service that helps individuals and families find solutions to their financial challenges. Following an initial consultation, clients receive a customized budget and action plan to get out of debt and achieve financial goals.</li>\r\n	<li>Debt Management Plans: help people get out of serious credit card debt by reducing the length of time it takes to repay the debt, eliminating fees and lowering interest.&nbsp;<span class="style15" style="color: rgb(255, 0, 0);">Start up fees will be waived for UFCW Local 99 members who engage in a debt management plan.</span></li>\r\n	<li>Student Loan Counseling: helps borrowers identify the right solution for paying off their student loans and guides them through numerous repayment options, step-by-step.</li>\r\n	<li>Housing Counseling: a free service that can help homeowners who are struggling to meet their mortgage payments.</li>\r\n	<li>Credit Look: provides consumers with a free credit score and a comprehensive review of their credit report. It&#39;s a great place to start for anyone who is unsure how to begin improving their financial situation.</li>\r\n</ul>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Should you need financial guidance, please call TCA at 1-800-823-7396 or visit&nbsp;<a href="http://www.takechargeamerica.org/" style="color: rgb(25, 32, 103);" target="_blank">www.takechargeamerica.org</a></p>\r\n', '', '2015-11-21 10:44:42', '65', 7, '40b4159591.png', '', '', '', '', '', NULL, 1),
(779, 'Fellows Tax & Financial Services', '20% discount on income tax preparation', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Tax planning and financial services.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><span class="style15" style="color: rgb(255, 0, 0);">20% discount on income tax preparation. On-line filing is now available at&nbsp;</span><a href="http://www.1040.com/fellowstaxservice/" style="color: rgb(25, 32, 103);" target="_blank">www.1040.com/fellowstaxservice</a>&nbsp;.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Call (623)979-1220 and ask for the UFCW Union discount. Union membership will be verified with the Union.</p>\r\n', '', '2015-11-21 10:46:02', '65', 7, '111f8e1484.png', '', '', '', '', '', NULL, 1),
(780, 'United Pet Care', 'Save 20-50% on every vet visit', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Taking care of your pet has never been easier or more affordable.</p>\r\n\r\n<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Enroll regardless of pet&#39;s age or medical condition</li>\r\n	<li>one year&#39;s membership costs less than a typical visit to a veterinarian</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Save 20-50% on every vet visit</li>\r\n	<li>Excellent selection of veterinarians</li>\r\n</ul>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Different plans to choose from. Call the Union office for additional information.</p>\r\n', '', '2015-11-21 10:47:54', '66', 7, '2fb58a66aa.png', '', '', '', '', '', NULL, 1),
(781, 'Union Plus Prescription Card', 'Save an average of 20% off brand name drugs and 55% off generic drugs', '<p class="style2" style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">This FREE program includes union members and their household members, including spouses, children, parents and extended family.</p>\r\n\r\n<p class="style2" style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">UFCW union members, retirees and their families &mdash; including spouses, children, parents and extended family &mdash; are eligible for this program. Health savings is not an insurance program, but it&#39;s a great supplemental plan to help you save on out-of-pocket medical costs.</p>\r\n\r\n<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Save an average of 20% off brand name drugs</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">55% off generic drugs</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">more than 53,000 pharmacies nationwide including Rite Aid, Safeway and many more.*</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Or get even greater savings when you choose to have convenient, money-saving mail-order prescriptions delivered right to your door. (*Discounts available at participating pharmacies only.)</li>\r\n</ul>\r\n\r\n<p class="style2" style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Union Plus Health Savings is a discount program, not insurance. Health Savings can sometimes help reduce out-of-pocket costs under some prescription programs, but it is not a replacement for a prescription drug insurance program.</p>\r\n\r\n<p class="style2" style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Go to&nbsp;<a href="http://www.unionplus.com/benefits/" style="color: rgb(25, 32, 103);" target="_blank">www.unionplus.com</a>&nbsp;and click on &quot;Health and Well Being&quot; and then &quot;Health Savings&quot;to receive your free card.</p>\r\n', '', '2015-11-21 10:49:20', '66', 7, 'f39294fd0b.png', '', '', '', '', '', NULL, 1),
(782, 'Union Plus Health Savings Program', 'For more information go to www.unionplus.com and click on "Health and Well Being" and then "Health Savings" or call (877) 570-4845', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">UFCW union members, retirees and their families &mdash; including spouses, children, parents and extended family &mdash; are eligible for this program. Health savings is not an insurance program, but it&#39;s a great supplemental plan to help you save on out-of-pocket medical costs.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Different programs to choose from.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><span class="style15" style="color: rgb(255, 0, 0);"><strong>Standard Program</strong><br />\r\n$14.95 per family per year OR ONE TIME fee of $4.95 when you join with the Union Plus Credit Card. Includes discounts on:</span></p>\r\n\r\n<ul class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Prescriptions</li>\r\n	<li>Vision care</li>\r\n	<li>Foot and ankle care</li>\r\n	<li>Hearing care</li>\r\n	<li>Diabetic supplies</li>\r\n</ul>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><strong>Premium Program</strong><br />\r\n$69.95 per family per year. Includes discounts on:</p>\r\n\r\n<ul class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Dental Care</li>\r\n	<li>Prescriptions</li>\r\n	<li>Vision care</li>\r\n	<li>Foot and ankle care</li>\r\n	<li>Hear care</li>\r\n	<li>Diabetic supplies</li>\r\n	<li>Nurse Hotline</li>\r\n</ul>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><strong>Premium Plus Program</strong>&nbsp;<br />\r\n$159.95 per year OR $15.95 per month. The Premium Plus program adds discounts with a large network of participating doctors, ancillary providers (labs, X-rays, MRIs and more), and hospitals. Includes discounts on:</p>\r\n\r\n<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Physician, hospital, ancillary services</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Dental care services</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Prescriptions</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Vision care</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Foot and ankle care</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Hearing care</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Diabetic supplies</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Nurse hotline</li>\r\n</ul>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">For more information go to&nbsp;<a href="http://www.unionplus.com/benefits/" style="color: rgb(25, 32, 103);">www.unionplus.com</a>&nbsp;and click on &quot;Health and Well Being&quot; and then &quot;Health Savings&quot; or call (877)570-4845</p>\r\n', '', '2015-11-21 10:50:30', '66', 7, 'e16b7f0641.png', '', '', '', '', '', NULL, 1),
(783, 'UFCW Local 99 Discount Dental Network', '', '<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">20% discount or no out of pocket expense (depending on your plan coverage, not exceeding the maximum plan benefit and provided you have coverage for the month of services.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">For Union members without coverage, a 20% discount will be given off of the total visit charge.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">For a list of providers who discount or waive &quot;out-of-pocket&quot; costs to Union members, click on one of the links below:</p>\r\n', '', '2015-11-21 10:52:36', '66', 7, '4f8a89e1e9.png', '', '', '', '', '', NULL, 1),
(784, 'UFCW Local 99 Discount Chiropractor Network', '', '<p style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Provider waives member&#39;s $20.00 co-pay (after deductible is satisfied). Must have current insurance coverage.</p>\r\n\r\n<p class="style2" style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">For a list of providers who discount or waive &quot;out-of-pocket&quot; costs to Union members, click on one of the links below:</p>\r\n', '', '2015-11-21 10:54:21', '66', 7, 'c11d9fdf31.png', '', '', '', '', '', NULL, 1),
(785, 'EMAP (Employee Assistance Program)', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">When you have a problem with:</p>\r\n\r\n<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Alcohol</li>\r\n	<li>drugs</li>\r\n	<li>child &amp; adolescent</li>\r\n	<li>family</li>\r\n	<li>gambling</li>\r\n	<li>marriage</li>\r\n	<li>financial/legal</li>\r\n</ul>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><span class="style15" style="color: rgb(255, 0, 0);">Get help! Open 24 hours a day, 7 days a week. (800)464-7101</span>.</p>\r\n', '', '2015-11-21 10:55:50', '66', 7, 'ba11bfcca4.jpg', '', '', '', '', '', NULL, 1),
(786, 'American Income Partners', '', '<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Up to 60% savings on:</p>\r\n\r\n<ul class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Eye Care</li>\r\n	<li>Hearing Aids</li>\r\n	<li>Prescription Drugs</li>\r\n	<li>Chiropractic Care</li>\r\n</ul>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">at no cost to the Union Member.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Contact your Union Representative for additional information and instructions.</p>\r\n', '', '2015-11-21 10:57:12', '66', 7, '67989b04d0.png', '', '', '', '', '', NULL, 1),
(787, 'AT&T Wireless', '', '<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Union members can receive a special 10% discount on wireless service from AT&amp;T (formerly Cingular), the only wireless company that can, and does, proclaim that it is &quot;Proud to Be Union.&quot;</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">The 10% discount is available off the regular monthly rate for any AT&amp;T individual or family plan.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Additional savings: Union members also receive additional special savings on phones and accessories.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Note: The 10% discount is not available on the iPhone.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><strong>3 ways to take advantage of your 10% AT&amp;T Wireless discount today</strong>:</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0);">If you have trouble with the coupon download, notify the AT&amp;T store that the Union Plus AT&amp;T Wireless Discount FAN number is: _____________ (Please call your Union office to request FAN number.)</p>\r\n\r\n<ol style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Find specials on phones and purchase service or add a line to your existing AT&amp;T Wireless account online.<em>You will be asked to certify that you are a current or retired union member prior to entering the AT&amp;T Web site. You will also be asked to provide your union name and local number at time of check out.</em></li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Call 800-897-7046 toll free. Purchase new service or add a line to your existing AT&amp;T Wireless account. Notify the AT&amp;T customer service operator that the Union Plus AT&amp;T Wireless Discount FAN number is: ______________ (Please call your Union office to request FAN number). You may be asked to fax proof of union membership if you call AT&amp;T customer service to purchase or change your existing service.</li>\r\n	<li class="style15" style="color: rgb(255, 0, 0);">Shop locally at an AT&amp;T store. Find an AT&amp;T store location near you here. Note: The union discount is ONLY available at AT&amp;T stores, NOT at any authorized dealer or kiosk to qualified members. Bring the savings coupon and one of the following acceptable forms of union identification to the store:\r\n	<ul>\r\n		<li>a union membership card</li>\r\n		<li>a Union Plus Credit Card</li>\r\n		<li>proof of payment of union dues</li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n', '', '2015-11-21 11:00:39', '68', 7, 'c4587cbb8a.png', '', '', '', '', '', NULL, 1),
(788, 'Sea Life Arizona', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Dive in and be wowed by the many strange, beautiful and fascinating creatures of the deep as you journey through the amazing underwater world of SEA LIFE Arizona. Prepare for astonishing close views of everything from the humble starfish to tropical sharks, all in displays which carefully recreate their natural habitats. To get any closer you&rsquo;ll have to get wet!</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Adult - $9.00 (gate price $19.67 (including tax) - you save $10.67)<br />\r\nChild - $9.00 (gate price $14.21 (including tax) - you save $4.21)<br />\r\nTickets can be purchased at the Union office.</p>\r\n', '', '2015-11-21 12:24:33', '61', 7, '851e77587e.png', '', '', '', '', '', NULL, 1),
(789, 'San Diego Wild Animal Park ', 'San Diego, California', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">At the San Diego Wild Animal Park you&#39;ll find yourself in the middle of Africa, where more than 3,500 rare and exotic animals roam several expansive habitats as large as 100 acres.</p>\r\n\r\n<p><span class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Adult - $45.00 (gate price $48.00 (you save $3.00)<br />\r\nChild - $35.00 (gate price $38.00 (you save $3.00)<br />\r\nTickets can be purchased at the Union office. (on-line purchase)</span></p>\r\n', '', '2015-11-21 12:25:25', '61', 7, '1eb557d255.png', '', '', '', '', '', NULL, 1),
(790, 'San Diego Zoo  ', 'San Diego, California', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">The San Diego Zoo is renowned as one of the best zoos in the world.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Adult - $45.00 (gate price $48.00 (you save $3.00)<br />\r\nChild - $35.00 (gate price $38.00 (you save $3.00)<br />\r\nTickets can be purchased at the Union office. (on-line purchase)</p>\r\n', '', '2015-11-21 12:27:10', '61', 7, 'd83eca48b3.png', '', '', '', '', '', NULL, 1),
(791, 'Red Rock Western Jeep Tours ', 'Sedona, Arizona', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Vortex, hiking, horseback, jeep and many other specialty tours.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">15% discount</p>\r\n', '', '2015-11-21 12:28:34', '61', 7, '2d79cf5e40.png', '', '', '', '', '', NULL, 1),
(792, 'Pirate''s Dinner Adventure ', 'Southern California', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">At&nbsp;<strong><em>Pirate&#39;s Dinner Adventure</em></strong>&nbsp;guests are entertained with an astonishing display of special effects wizardry, aerial artistry, swashbuckling swordplay, dynamic duels and daring-do; a classic story of good vs. evil that offers the perfect blend of action, adventure, comedy and romance; the opportunity to interact in the adventure; and a sumptuous dining experience.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">The show is perfect for children and adults alike, so take a few minutes to check out the rest of our site, then fill out our reservation form to reserve your space for a pirate adventure you will always remember. Pirate&rsquo;s Dinner Adventure is acclaimed as&nbsp;<strong><em>&ldquo;the world&rsquo;s most interactive dinner show!&rdquo;</em></strong></p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">$4.00 off of adult admission and $2.00 off of child admission. Coupon is good for up to 6 guests. Request your coupon from the Union office.</p>\r\n', '', '2015-11-21 12:29:20', '61', 7, 'fb77be26d5.png', '', '', '', '', '', NULL, 1),
(793, 'Palm Springs Aerial Tramway  ', 'Palm Springs, California', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Ascend two and one half miles to a pristine wilderness aboard the&nbsp;<em><strong>World&#39;s Largest Rotating Tramcars</strong></em>.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">15% off Tram admission. 10% off at gift shops and restaurant purchases. Request coupons from the Union office.</p>\r\n', '', '2015-11-21 12:30:08', '61', 7, '46fee10dc7.png', '', '', '', '', '', NULL, 1),
(794, 'Out of Africa Wildlife Park ', 'Camp Verde, Arizona', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Arizona&rsquo;s Best Wildlife Theme Park. See over 400 animals from all over the world!</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Lions, Tigers, Giraffe, Rhinoceros, Black Bear, Camel, Ostrich, Cobras, Zebra, Exotic Birds, Tortoise, Wildebeest, Jaguar, Leopards, Wolves, Hyenas and more!</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><strong>Tiger Splash</strong>: You&rsquo;ll &ldquo;oooh and aaah&rdquo; while Bengal and Siberian tigers and other big cats interact in a predator/prey relationship of romping and splashing in a 35-foot by 50-foot pool as they play with their caretakers and various colorful toys. This unique, thrilling, and exciting show is also very educational. Through the live narration, you&rsquo;ll discover how instincts, intellect and feelings interact to form spontaneous, natural behavior.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><strong>Serengeti Safari</strong>: On the 45-minute Serengeti Safari, you will experience the heart of Africa in the heart of Arizona. This is an authentic African photo safari with expert park guides narrating throughout your tour. While riding in a safari vehicle, you can expect a personal encounter with giraffe, zebra, ostrich and more. We provide treats so that you can feed the animals and maybe receive a gentle kiss from a giraffe. On nearby hills, prides of lions or other big cats overlook their domain, you, and the prey animals in the Serengeti. It&rsquo;s like Africa, only closer!</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Adult - $26.36 (gate price $29.95+10% tax=$32.95 - you save $6.59)<br />\r\nChild - $13.16 (gate price $14.95+10% tax=$16.45 - you save $3.29)<br />\r\nTickets can be purchased at the Union office</p>\r\n', '', '2015-11-21 12:32:09', '61', 7, '2bd69ff38a.jpg', '', '', '', '', '', NULL, 1),
(795, 'Medieval Times ', 'Southern California', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Not far from Hollywood is another legendary entertainment mecca&mdash;our California Castle. The fortress-like, 1,116 seat Spanish palace is right at home with the red tile roofs, palm trees and blazing sun of Southern California. And its location couldn&#39;t be better. The castle is only minutes from Disneyland, near the Santa Ana and Artesia freeways. Most of all, we&rsquo;re proud to say that our show&rsquo;s stunning battle sequences and authentic Medieval tournament offer one thing Hollywood doesn&#39;t: the thrill of live action.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">15% savings off regular admission, excluding Saturdays, and 10% savings off merchandise in the gift shops. Request coupons from the Union office.</p>\r\n', '', '2015-11-21 12:33:13', '61', 7, '5a198d4b82.png', '', '', '', '', '', NULL, 1),
(796, 'LEGOLAND/ SEALIFE  ', 'Southern California', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">LEGOLAND&reg; California brings out the hero in everyone.<br />\r\nThere are more than 50 super rides, shows and attractions<br />\r\nfor the whole family.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">SEA LIFE&trade; Aquarium is an educational and interactive dynamic experience focusing on a child&rsquo;s voyage to the depths of the oceans, presenting the wonders of the underwater world to them in a way specially designed for their understanding.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">LEGOLAND Only:<br />\r\nAdult - $71.00 (gate price $85.00 - you save $14.00)<br />\r\nChild (ages 3-12) - $66.00 (gate price $79.00 - you save $13.00)<br />\r\n2nd Day FREE to Legoland (second visit must be used within 90 days of first visit.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">LEGOLAND Resort Hopper Ticket (1 day hopper plus a FREE 2nd day to all three attractions (on-line purchase).<br />\r\nIncludes admission to:<br />\r\nLegoLand California<br />\r\nSeaLife Aquarium<br />\r\nLegoLand Water Park*&nbsp;<br />\r\nAdult - $76.00 (gate price $109.00 - you save $33.00)<br />\r\nChild (ages 3-12) - $71.00 (gate price $103.00 - you save $32.00)</p>\r\n', '', '2015-11-21 12:34:16', '61', 7, 'cf7b756cb3.png', '', '', '', '', '', NULL, 1),
(798, 'Las Vegas: 1 day Power Pass ', 'Las Vegas, Nevada', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Present your Power Pass at the Ticket window at the following attractions for FREE Admission:</p>\r\n\r\n<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li><em>Star Trek: The Experience</em></li>\r\n	<li><em>Madame Tussaud&#39;s Interactive Wax Attraction</em></li>\r\n	<li><em>Guggenheim Hermitage Museum</em></li>\r\n	<li><em>Bellagio Gallery of Fine Art</em></li>\r\n	<li><em>Stratosphere Tower</em></li>\r\n	<li><em>King Tut&#39;s Museum</em></li>\r\n	<li><em>In Search of the Obelisk</em></li>\r\n	<li><em>Manhattan Express Roller Coaster</em></li>\r\n	<li><em>Titanic Exhibit at the Tropicana Hotel</em></li>\r\n	<li><em>Siegfried and Roy&#39;s Secret Garden and Dolphin Habitat</em></li>\r\n	<li><em>Atomic Testing Museum</em></li>\r\n	<li><em>Liberace Museum</em></li>\r\n	<li><em>Elvis-a-rama</em></li>\r\n	<li><em>Nevada State Museum</em></li>\r\n	<li><em>Lost Vegas Gambling Museum</em></li>\r\n	<li><em>Lied Children&#39;s Museum</em></li>\r\n	<li><em>Las Vegas Art Museum</em></li>\r\n	<li><em>Las Vegas Natural History Museum</em></li>\r\n	<li><em>Bonnie Springs-Old Nevada</em></li>\r\n	<li><em>Las Vegas Zoo</em></li>\r\n</ul>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Also includes special offers and discounts at a variety of shops, restaurants and retailers, and sightseeing tours.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Adult - $71.25, Child - $40.52<br />\r\nContact the Union office for complete information and ID Code.</p>\r\n', '', '2015-11-21 12:35:35', '61', 7, 'acdfc9a214.png', '', '', '', '', '', NULL, 1),
(799, 'Lake Powell Resort/Aramark  ', 'Page, Arizona', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Explore the enchanting world of Glen Canyon National Recreation Area and Lake Powell in your own vacation houseboat. This breathtaking landscape - extending into both Arizona and Utah - is like no other place on earth.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">More than 400 feet deep, 186 miles long and nearly 2,000 miles of shoreline, Lake Powell surrounds you with spectacular rock canyons and towers. It embraces you with rosy sand beaches and gently rocks you to sleep under night skies shimmering with stars.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Save up to 20% off at lake Powell Resorts and Marinas.&nbsp;<br />\r\n10% off Lodging, Boat Tours &amp; Powerboats.<br />\r\n20% off Adventurer &amp; Discover Class Houseboats&nbsp;<br />\r\n<em><strong>Excludes 7/1/08 to 8/18/08.</strong></em></p>\r\n', '', '2015-11-21 12:36:56', '61', 7, 'a0b7868cf5.png', '', '', '', '', '', NULL, 1),
(800, 'Knott''s Berry Farm ', 'Southern California', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">What was once an actual berry farm is now 160 acres of world class rides, unique family shows and one of a kind attractions. At America&#39;s favorite theme park, experience Ghost Rider the world&#39;s best wooden roller coaster, Supreme Scream, 30-stories up, 3 seconds down and the high-octane thrill of Xcelerator, rated the world&#39;s best launch coaster, sending riders 0-80 in 2.3 seconds. For a different type of thrill, stroll on over to Camp Snoopy, home to the beloved PEANUTS gang.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Adult - $37.00 (gate price $65.00 - you save $28.00)<br />\r\nChild (ages 3 and up, under 48&quot; tall) - $30.00 (gate price $35.00 - you save $5.00)</p>\r\n', '', '2015-11-21 12:38:10', '61', 7, '836c0280a7.png', '', '', '', '', '', NULL, 1),
(801, 'Grand Canyon Railway & Resort ', 'Williams, Arizona ', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">All aboard to Grand Canyon National Park! Journey to Grand Canyon National Park aboard Grand Canyon Railway and take a trip not only to America&#39;s most recognized spectacle, but a trip back in time.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">20% discount on</p>\r\n', '', '2015-11-21 12:39:04', '61', 7, '2b5ae4acf7.png', '', '', '', '', '', NULL, 1),
(802, 'Golfland Entertainment Centers:  Sunsplash', 'Mesa Arizona', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Water rides and cool attractions, adventure, relaxation, and fun for everyone in the family.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">General Admission: $20.00 (gate price is $28.99)</p>\r\n', '', '2015-11-21 12:40:09', '61', 7, '2573ed21c2.png', '', '', '', '', '', NULL, 1),
(803, 'Disneyland and California Adventure ', 'Southern California', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><a href="http://disneyland.disney.go.com/disneyland/en_US/parks/landing?name=DisneylandParkLandingPage" style="color: rgb(25, 32, 103);" target="_blank">Disneyland</a>: Experience the magic of two enchanting theme parks &ndash; Disneyland&reg; Park and Disney&#39;s California Adventure&reg; Park &ndash; only at the Disneyland&reg; Resort in California. Fill your days and nights with the wonder and excitement of two distinct Southern California theme parks.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><a href="http://disneyland.disney.go.com/disneyland/en_US/parks/landing?name=DisneysCaliforniaAdventureLandingPage" style="color: rgb(25, 32, 103);" target="_blank">California Adventure</a>: Immerse yourself in a magical festival of shows, parades and attractions celebrating California&#39;s storied past and exciting future. From the gold rush to Hollywood&#39;s golden era to the timeless allure of the beach, discover the vast and diverse Golden State.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">2-Day - Hopper:<br />\r\nAdult - $215.75 (gate price $225.00 - you save $9.25)<br />\r\nChild (3 - 9 yrs) - $203.40 (gate price $212.00 - you save $8.60)</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">2-Day - One park per day:<br />\r\nAdult - 175,75 (gate price $185.00 - you save $9.25)<br />\r\nChild (3-9 yrs) - $163.40 (gate price $172.00 - you save $8.60)</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Tickets can be purchased at the Union office. (on-line purchase)</p>\r\n', '', '2015-11-21 12:41:27', '61', 7, '976a890d00.png', '', '', '', '', '', NULL, 1),
(804, 'Catalina Channel Express ', 'Southern California', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><em><strong>Are you ready to experience enchantment and beauty on Catalina Island?</strong></em>&nbsp;The adventure begins on board Catalina Express where sitting on an outside upper deck you can take in ocean breezes or keep your eyes peeled to catch a glimpse of White-sided dolphins. Or come inside and enjoy a snack or refreshing soda or cocktail as you relax in airline style seats</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">$5.00 off an adult round trip ticket. Request coupons from the Union office.</p>\r\n', '', '2015-11-21 12:42:14', '61', 7, '69957d2bde.png', '', '', '', '', '', NULL, 1),
(805, 'Castles n'' Coasters ', ' Phoenix, Arizona', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Whether you&#39;re young or old, big or small, you&#39;ll find lots of fun, food and excitement at Arizona&#39;s Finest Family Fun and Thrill Park.<br />\r\nFrom the thrilling sky high&nbsp;<a href="http://www.castlesncoasters.com/desert.html" style="color: rgb(25, 32, 103);" target="_blank">roller coasters</a>&nbsp;and exciting&nbsp;<a href="http://www.castlesncoasters.com/splash.html" style="color: rgb(25, 32, 103);" target="_blank">water rides</a>, to the exotic countries of the world class&nbsp;<a href="http://www.castlesncoasters.com/golf.html" style="color: rgb(25, 32, 103);" target="_blank">miniature golf</a>course and enchantment of an old fashioned&nbsp;<a href="http://www.castlesncoasters.com/carrousel.html" style="color: rgb(25, 32, 103);" target="_blank">carrousel</a>, there&#39;s magic and new adventures waiting for you and your family at Castles N&#39; Coasters.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">All day ride pass and one round of golf: $22.50 (plus tax) for adult and child ticket (gate price is $30.00). Purchase tickets at the Union office. (on-line purchase)</p>\r\n', '', '2015-11-21 12:43:33', '61', 7, '859fe15fb3.png', '', '', '', '', '', NULL, 1),
(807, 'Arizona Sonora Desert Museum ', 'Tucson, Arizona', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">For a half-century the Arizona-Sonora Desert Museum has enchanted millions of visitors with its exhibits of live animals in astonishingly natural settings, while intriguing and instructing them with fascinating educational programs. At the same time, the Museum has gained a worldwide repute in the scientific community as an institution committed to researching and protecting the land, plants, and the animals of the Sonoran Desert region.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">$2.00 off of admission. Request coupons from the Union office.</p>\r\n', '', '2015-11-21 12:45:19', '61', 7, 'c82db6a5fb.png', '', '', '', '', '', NULL, 1),
(808, 'Aquarium of the Pacific ', 'Long Beach, California', '<p align="left" style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Come face-to-face with, and even touch, the ocean&rsquo;s ultimate predators in Shark Lagoon. Hand feed lorikeet birds. Join us on a Behind-the-Scenes tour. Enjoy Monsters of the Abyss, a film that combines stunning state-of-the-art 3-D technology and digital animation to explore the deep ocean.</p>\r\n\r\n<p align="left" class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">$3.00 off on adult tickets and $1.50 off child (3-11) tickets. Save 10% off gift store purchases. May not be combined with any other offer. Request coupon from the Union office.</p>\r\n', '', '2015-11-21 12:47:04', '61', 7, 'ddf09d5db2.png', '', '', '', '', '', NULL, 1),
(809, 'Union Plus College Loans', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Available to Union members and/or their dependents who are attending accredited colleges and universities.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Many families today need help paying for college. If you or your child plan to attend an accredited college or university at least part-time, then the Union Plus Education Loans through American Education Services (AES) are your best bet.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Borrowing through the Union Plus Education Loan program gives you access to many financial products. They can provide low-cost federal loans and private loans with rate reductions for automatic and on-time payments. You can borrow all of the money you need-for tuition, books, fees, computers and other education-related expenses.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Unique benefits exclusively for union members:</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Strike benefit: If you have a Union Plus Education Loan and have been on strike for 30 days or longer, payments can be made for you up to 60 days if your account is eligible and in good standing. For more information, call 1-877-881-1022.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">UPromise Advantage: By shopping at participating stores, you can earn contributions for future college expenses or payments to your current student loans. Learn more about this special program here.</p>\r\n', '', '2015-11-21 12:56:50', '69', 7, '6e39a6cf6f.png', '', '', '', '', '', NULL, 1),
(810, 'Union Plus Scholarship Program', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">$500.00 up to $4,000.00 scholarship grants.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Students of working families are eligible for various union-sponsored scholarships. Members can look into these programs and apply for wonderful opportunities that make continuing education more affordable.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">To receive more information, go on-line to http://www.unionplus.org/benefits/education/scholarships or call 1-877-881-1022.1-877-881-1022.</p>\r\n', '', '2015-11-21 12:57:24', '69', 7, '373cd69a53.png', '', '', '', '', '', NULL, 1),
(811, 'UFCW Local 99 Scholarships', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">UFCW Local 99 is pleased to announce the continuation of its college and vocational Scholarship Awards Program for the academic school year 2013-2014.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">The union grants scholarships in the belief that it can make no finer investment than to provide better educational opportunities for deserving students.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">Every member can take personal pride in the growth and development of this outstanding education assistance program.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">The following are examples of the possible scholarship awards to be granted.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">COLLEGE</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">Full-Time<br />\r\nOne Award: $3,000<br />\r\nOne Award: $2,000<br />\r\nFour Awards: $1,500 each<br />\r\nTen Awards: $1,000 each</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">Part-Time<br />\r\nFour Awards: $750 each</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">VOCATIONAL</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">Two Awards: $500 each</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">Testing will take place on a date and time to be determined. Applicants will receive a notice by mail of the date, time and place prior to the testing date.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal; background-color: rgb(255, 255, 153);">BRING A DRIVER&#39;S LICENSE OR PICTURE I.D. AND A #2 PENCIL.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Rules and Eligibility</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">(see ufcw99.org for complete details)</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Applicants must establish basic eligibility by applying for a scholarship award and completing the UFCW LOCAL 99 SCHOLARSHIP AWARDS COMPETITION APPLICATION published in the 99Report.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Send it to:&nbsp;<br />\r\nUFCW Local 99<br />\r\nAttn: Scholarship Desk<br />\r\n2401 N. Central Ave., 2nd Floor Phoenix, AZ 85004.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">&nbsp;</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><span style="color: rgb(25, 32, 103); font-family: Times; font-size: medium; font-weight: bold; line-height: normal; text-decoration: underline;">DOWNLOAD SCHOLARSHIP APPLICATION</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">&nbsp;</p>\r\n', '', '2015-11-21 13:00:17', '69', 7, '98f44d99e6.jpg', '', '', '', '', '', NULL, 1),
(812, 'UFCW International Union Scholarships', '', '<p><span style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Scholarship awards up to $4,000.00.&nbsp;</span><a href="http://www.ufcw.org/scholarship/" style="color: rgb(25, 32, 103); font-family: Times; font-size: medium; line-height: normal;" target="_blank">Apply on-line at www.ufcw.org/ scholarship&nbsp;</a><span style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">or call UFCW Local 99 and request an International Scholarship Application.</span></p>\r\n', '', '2015-11-21 13:01:37', '69', 7, '1396fd8fdf.png', '', '', '', '', '', NULL, 1),
(813, 'Chancellor University', '', '<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Chancellor will offer a 10% tuition discount for members</li>\r\n	<li>Chancellor will waive the $50 application fee for members</li>\r\n</ul>\r\n', '', '2015-11-21 13:02:35', '69', 7, '7b5e4afe3f.png', '', '', '', '', '', NULL, 1),
(814, 'Quality Equipment and Spray Pest Control', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Pest control supplies and services.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">20% discount on all purchases or $50 off annual professional services.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Contact Andrew Greese at (602)371-1993, (480)496-9200 or (800)675-7485 or e-mail:&nbsp;<a href="mailto:info@qspry.com" style="color: rgb(25, 32, 103);" target="_blank">info@qspry.com</a>.</p>\r\n', '', '2015-11-21 13:11:11', '72', 7, 'f6349d7a0b.png', '', '', '', '', '', NULL, 1),
(815, 'Protection One', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Home and business security systems and monitoring.</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Monitored Smoke Detector device and 3 months free monitoring ($300 value).</p>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Must call Marlys Sixsmith at 480-529-8159 and request ESM/UFCW discount. Must show proof of Union membership.</p>\r\n', '', '2015-11-21 13:12:21', '72', 7, 'f9cf4fab73.png', '', '', '', '', '', NULL, 1),
(816, 'Patriot Pest & Termite Control', '', '<p><span style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">10% discount on complete pest and termite control. Call (602)861-2266. Must show proof of Union membership.</span></p>\r\n', '', '2015-11-21 13:13:57', '72', 7, '2580a15aba.png', '', '', '', '', '', NULL, 1),
(817, 'Drain Squad', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Drain and sewer cleaning, slab leaks, water heaters, water and sewer line repairs, backflow prevention services.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">15% discount off total bill.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Contact Raymond Perez (623)594-4333 or (480)393-3290. Must request Union discount and show proof of Union membership.</p>\r\n', '', '2015-11-21 13:15:39', '72', 7, 'e7c0c4e81c.png', '', '', '', '', '', NULL, 1),
(818, 'Dial One Mears Air Conditioning & Heating, Inc.', '', '<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">15% discount on parts and labor when you need service on air conditioning and heating system.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Must show proof of Union Membership.</p>\r\n', '', '2015-11-21 13:16:32', '72', 7, '1c89be46bf.png', '', '', '', '', '', NULL, 1),
