(820, 'Union Lawyer Referral', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">You and your family are entitled to receive legal services at a discounted rate.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Discounts offered in the following areas of law:</p>\r\n\r\n<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Accident/Injury</li>\r\n	<li>Insurance Claims</li>\r\n	<li>Drunk Driving</li>\r\n	<li>Wills and Real Estate</li>\r\n	<li>Immigration Matters</li>\r\n	<li>Criminal Defense</li>\r\n	<li>Divorce</li>\r\n	<li>Bankruptcy</li>\r\n	<li>Business Matters</li>\r\n</ul>\r\n\r\n<p class="style15" style="color: rgb(255, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Call (800)244-0661. The receptionist will ask for your Social Security number which will be used to verify Union membership.</p>\r\n', '', '2015-11-21 13:23:02', '75', 7, 'b81b790cc0.png', '', '', '', '', '', NULL, 1),
(821, 'Labor''s Community Service Agency', '', '<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;"><em>Are you behind on your mortgage?&nbsp;</em>H.U.D. Certified Housing Counselors can help with:</p>\r\n\r\n<ul style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">\r\n	<li>Advocacy for reinstatement of your loan</li>\r\n	<li>Money management counseling</li>\r\n	<li>Financial assistance for qualified homeowners</li>\r\n</ul>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">If you are 2 or more months behind, contact our Foreclosure Intervention Counselors today!&nbsp;<strong>ALL SERVICES ARE PROVIDED FREE</strong>.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Times; font-size: medium; line-height: normal;">Call (602)263-5741</p>\r\n', '', '2015-11-21 13:26:02', '76', 7, '3737774d5e.png', '', '', '', '', '', NULL, 1),
