INSERT INTO `app` (`app_id`, `about_us`, `app_name`, `applicationID`, `clientKey`, `restKey`, `masterKey`, `pushCount`) VALUES
(1, 'United Food and Commercial Workers Local 5 is a 33,000 member labor union based in San Jose, California. The local is a progressive voice in the labor movement and the community with a strong tradition of member advocacy and involvement. It is an affiliate of the United Food and Commercial Workers International union based in Washington DC.\r\n\r\nLocal 5 members work primarily in retail grocery and meat with growing numbers employed in department stores.', 'UFCW 5', 'Lr8VBQHTUyzw4RoFpcyHQnCcHJAQb1PPhBVzDtqK', 'CHhHCo1TO0shdXvmhFywuNbFDVpalWvj9s6FqcM2', 'BfhbRn0mcXST0vxbSmLDgacHejVexmdBtnThCboZ', 'sOmMyLBOBSStGbiGL5EEAptl5G1H5fqQG7Os7NyF', 9),
(2, 'The United Latinos of the United Food and Commercial Workers Union (UFCW) is an organization of men and women who have joined together to promote the issues and pursue interests important to Latino workers.\r\n“Working Toward Latino Empowerment and Building Latino Pride!” We have a simple but powerful purpose of empowering Latino men and women within the UFCW and within our communities. We are also building Latino pride which will help others better understand our cultural differences.', 'UNITED LATINOS', 'w6aXlGWvcnOucmWTO2yfPpUgn7uqhKy6QBKc87bd', 'Wb2OJxkWkX0ryg1BMzkyjndcQqD9AVcLH7foaCv2', 'nn56EVWGtNaQ0HvDc8tvzMDUYxWujClEQoOJpW6i', 'n3MTsDZAdPhewQBVNbMFI6hndo99LhQGIVE59JPV', 3),
(3, 'For decades in Santa Clara and San Benito County, the Building & Construction Trades Council has been the driving force for excellence in the construction industry in the bay area.\r\n\r\nThe twenty-three craft unions affiliated with the council represent over 30,000 highly trained, skilled construction industry workers employed in Silicon Valley.\r\n\r\nThrough the efforts of the Building Trades Council, Silicon Valley has enjoyed the highest level of professionalism and training in step with the increasingly demanding requirements of the region''s high technology companies and developers. \r\n\r\nUnion crafts people are twenty-nine percent more productive than or non-union counterparts.\r\nFor a job done on time, at budget and the best quality, build it union.', 'SCBTC Super Group App', 'orFbBISE4eO088Q8l7fz0mnLfeeWdlCY5rRvgYnN', 'u29Wi6lOewPSvIA3wLoYGs1AJJjDWEfqbqZor90C', 'BRaxAlwaiQm1xKbIASNcgxJuAUF3MxS6xIA1WVzF', 'HpqCyGu2kDzkoJTbDollXntQn4Dj2q119oY5X0ic', 3),
(4, 'NO ABOUT US CONTENT.', 'UFCW648', 'dKW1ohEaNhqHYB3TldVN3n3JKlburbQOROC2l6IP', 'GLBO1Ci41uqXY4OG4s6j6XD6zhuUaO68wT1WFhLB', 'JGWvpqBP6XlJoc4y9B7wflXfzIg1sfAf3j6nV4Xm', 'IzWyOzuHW5FGXW7FrxmJ6iDuVcwJSsN9prww8nm6', 3),
(5, 'SouthBay Labor About Us', 'SouthBay Labor', 'GRXnk62l7nR7QR85eG5rJzy48uNOBt4LsOJ6FI9o', 'PRhdwMNe1YMDAw9BLf9EebxW7XDRkHRqswTywcbk', 'BR3o0xWvrflkQX7kr98A14VVQeiyE8Aix8b7XAI6', 'E9ZAX0QVUsFDphzg6EUtJfiFBlfXKiXTQBZXsJ1E', 3),
(6, 'YOUR UNION\nPREPARING TODAY \nFOR THE NEEDS OF TOMORROW', 'UFCW 480', 'cMTYrV8ByHQfkcY41QJjycAd79tCmiXNmgBFyDUI', 'bfDcObs9bxq0X0MFHCXxJ2M2zrASxemAK4gzlKDu', 'neDRHapbl1D5ifAR7OTnDElOXKSYnCUsZ1iWgymb', 'Ove7YvDqFL5BP7gUhyOj2DoTHRsZjIICa1MXc64e', 5),
(7, 'The United Food and Commercial Workers Union (UFCW) is one of the largest and strongest private sector Unions in North America.  UFCW Members work in dozens of industries:\r\n\r\nretail food stores\r\nfood processing\r\npoultry processing\r\nmeat packing plants\r\ndepartment stores\r\nbanks\r\nchemical and manufacturing plants\r\ndistilleries\r\ntextile plants\r\nhospitals\r\nand many more workplaces.\r\n\r\nThe UFCW is made up of more than 1.4 million people like you, people working together to improve their lives. The union - and its resources - stand with the members.\r\n\r\nThe UFCW is a strong union, with an informed, active and loyal membership and a progressive and responsive leadership.  We are a union whose voice is heard and respected in the labor movement, in Washington, D.C. and Ottawa, in state and provincial capitals, and in international forums overseas: a union that serves its members and that is respected by employers at the collective bargaining table. \r\n\r\nThe UFCW has a national office in Washington, D.C. and more than 900 local unions throughout the United States, Canada, and Puerto Rico.\r\n\r\nUFCW Local 99 is the largest private sector union in the State of Arizona.\r\n\r\nThe following is a list of companies in Arizona which are currently represented by UFCW Local 99 Collective Bargaining Agreements.', 'UFCW 99', 'Y72zyeiu02Lys7tDVKe2FGJtmvcpsErVXqb4tdlY', 'w58pG8MOzkRpMwdmORlOI9TRJz5raKgKqIB9sLVA', 'RD9f2XnJJnnuRQYZXn1GkniU4abzfR3CAOfT9tDN', 'cylrJ834pUOAn5qd7WcuX6G8bIwf9Pq60eIUGHES', 10);
